import React, { useState, useEffect } from 'react';
import { useLocation } from 'react-router-dom';
import { IoCloseOutline } from "react-icons/io5";

const lineImages = {
    1001: require('../../assets/img/subwaymapmarker/1Ìò∏ÏÑ†ÎßàÏª§.png'),
    1002: require('../../assets/img/subwaymapmarker/2Ìò∏ÏÑ†ÎßàÏª§.png'),
    1003: require('../../assets/img/subwaymapmarker/3Ìò∏ÏÑ†ÎßàÏª§.png'),
    1004: require('../../assets/img/subwaymapmarker/4Ìò∏ÏÑ†ÎßàÏª§.png'),
    1005: require('../../assets/img/subwaymapmarker/5Ìò∏ÏÑ†ÎßàÏª§.png'),
    1006: require('../../assets/img/subwaymapmarker/6Ìò∏ÏÑ†ÎßàÏª§.png'),
    1007: require('../../assets/img/subwaymapmarker/7Ìò∏ÏÑ†ÎßàÏª§.png'),
    1008: require('../../assets/img/subwaymapmarker/8Ìò∏ÏÑ†ÎßàÏª§.png'),
    1009: require('../../assets/img/subwaymapmarker/9Ìò∏ÏÑ†ÎßàÏª§.png'),
    // Îã§Î•∏ ÏßÄÌïòÏ≤† Ìò∏ÏÑ†Ïóê ÎåÄÌïú Ïù¥ÎØ∏ÏßÄ Í≤ΩÎ°ú Ï∂îÍ∞Ä
};

const Right = ({ isVisible, toggleRightVisibility, stationName, stationLine }) => {
    const handleClose = () => {
        toggleRightVisibility();
    };

    const [stationId, setStationId] = useState(null);
    const [congestionData, setCongestionData] = useState(null);
    const location = useLocation();
    const [arrivalInfo, setArrivalInfo] = useState(null); // ÎèÑÏ∞© Ï†ïÎ≥¥ ÏÉÅÌÉú
    const printedDirections = [];

    useEffect(() => {
        const fetchStationId = async () => {
            try {
                // Î™®Îì† ÏßÄÌïòÏ≤†Ïó≠ Ï†ïÎ≥¥ Í∞ÄÏ†∏Ïò§Í∏∞
                const response = await fetch('https://apis.openapi.sk.com/puzzle/subway/meta/stations');
                if (!response.ok) {
                    throw new Error('Network response was not ok');
                }
                const stations = await response.json();

                // ÏÑ†ÌÉùÌïú Ïó≠ Ïù¥Î¶ÑÍ≥º ÎßûÎäî stationId Ï∞æÍ∏∞
                const selectedStation = stations.find(station => station.name === stationName);
                if (selectedStation) {
                    setStationId(selectedStation.id);

                    // stationIdÎ•º Ïù¥Ïö©ÌïòÏó¨ ÌòºÏû°ÎèÑ Ï†ïÎ≥¥ Í∞ÄÏ†∏Ïò§Í∏∞
                    const congestionResponse = await fetch(`https://apis.openapi.sk.com/puzzle/subway/congestion/stat/car/stations/${selectedStation.id}?dow=TUE&hh=08`, {
                        method: 'GET',
                        headers: {
                            'appkey': 'PEKSyFHvkl5imbyfUyw8D2gFUcgqSnxu4d3c8keS'
                        }
                    });
                    if (!congestionResponse.ok) {
                        throw new Error('Network response was not ok');
                    }
                    const congestionData = await congestionResponse.json();
                    setCongestionData(congestionData);
                }
            } catch (error) {
                console.error('There was a problem with your fetch operation:', error);
            }
        };

        fetchStationId();
    }, [stationName]);

    const fetchDataFromFlask = async () => {
        try {
            // ÏöîÏ≤≠ÏùÑ Î≥¥ÎÉÑ
            const response = await fetch("http://flask:8082/receive_subway_arrive", {
                method: "POST",
                headers: {
                    "Content-Type": "application/json",
                },
                body: JSON.stringify({ "stationName": stationName }), // Îç∞Ïù¥ÌÑ∞Î•º JSON Î¨∏ÏûêÏó¥Î°ú Î≥ÄÌôòÌïòÏó¨ Ï†ÑÏÜ°
            });

            // ÏùëÎãµÏùÑ JSON ÌòïÏãùÏúºÎ°ú ÌååÏã±
            const data = await response.json();

            // Î∞õÏïÑÏò® ÎèÑÏ∞© Ï†ïÎ≥¥Î•º ÏÉÅÌÉúÏóê Ï†ÄÏû•
            setArrivalInfo(data);
        } catch (error) {
            console.error('Îç∞Ïù¥ÌÑ∞ Í∞ÄÏ†∏Ïò§Í∏∞ Ïã§Ìå®:', error);
        }
    };
    // Ïª¥Ìè¨ÎÑåÌä∏Í∞Ä ÎßàÏö¥Ìä∏Îê† Îïå Îç∞Ïù¥ÌÑ∞ Í∞ÄÏ†∏Ïò§Í∏∞
    useEffect(() => {
        fetchDataFromFlask();
    }, [stationName]); // stationNameÏù¥ Î≥ÄÍ≤ΩÎê† ÎïåÎßàÎã§ fetchDataFromFlask Ìò∏Ï∂ú


    function getLineImage(subwayId) {
        return lineImages[subwayId];
    }
    function getSubwayLineName(subwayId) {
        switch(subwayId) {
            case "1001":
                return "1Ìò∏ÏÑ†";
            case "1002":
                return "2Ìò∏ÏÑ†";
            case "1003":
                return "3Ìò∏ÏÑ†";
            case "1004":
                return "4Ìò∏ÏÑ†";
            case "1005":
                return "5Ìò∏ÏÑ†";
            case "1006":
                return "6Ìò∏ÏÑ†";
            case "1007":
                return "7Ìò∏ÏÑ†";
            case "1008":
                return "8Ìò∏ÏÑ†";
            case 1009:
                return "9Ìò∏ÏÑ†";
            // Îã§Î•∏ Ìò∏ÏÑ†Ïóê ÎåÄÌïú Í≤ΩÏö∞ÎèÑ Ï∂îÍ∞Ä
            default:
                return "Ìï¥ÎãπÏóÜÏùå";
        }
    }
    const groupedData = (arrivalInfo || []).reduce((acc, info) => {
        if (!acc[info.subwayId]) {
            acc[info.subwayId] = [];
        }
        acc[info.subwayId].push(info);
        return acc;
    }, {});
    return (
        <div id='right' className={isVisible ? '' : 'hidden'}>
            <div className="close-button-container">
                <button className="close-button" onClick={handleClose}><IoCloseOutline/></button>
            </div>
            {stationName && (
                <div>
                    <br/>
                    <h2>üí° {stationName}Ïùò Ïã§ÏãúÍ∞Ñ ÎèÑÏ∞© Ï†ïÎ≥¥</h2>
                    <br/>
                    <br/>
                    {/* arrivalInfoÍ∞Ä Ï°¥Ïû¨ÌïòÎäî Í≤ΩÏö∞ÏóêÎßå ÏïÑÎûò ÎÇ¥Ïö©ÏùÑ ÌëúÏãú */}
                    {arrivalInfo && (
                        <div style={{ display: 'flex', flexDirection: 'column', alignItems: 'flex-start' }}>
                            {/* arrivalInfoÎ•º ÏàúÌöåÌïòÎ©∞ Í∞Å ÎèÑÏ∞© Ï†ïÎ≥¥Î•º Ï∂úÎ†• */}
                                        {/* Ïó¥Ï∞® Ï†ïÎ≥¥ÏôÄ ÎèÑÏ∞© Ï†ïÎ≥¥ */}
                            {Object.keys(groupedData).map(subwayId => (
                                <div key={subwayId}>
                                    <p>
                                        <img
                                            style={{ width: '20px', height: 'auto', verticalAlign: 'middle' }}
                                            src={getLineImage(subwayId)}
                                            alt="Subway Line Marker"
                                        />
                                        {getSubwayLineName(subwayId)}
                                    </p>
                                    <div style={{ display: 'flex', flexWrap: 'wrap' }}>
                                        {groupedData[subwayId].map((info, index) => {
                                            // trainLineNmÏùÑ "-" Í∏∞Ï§ÄÏúºÎ°ú Î∂ÑÎ¶¨ÌïòÏó¨ Î∞©Î©¥Í≥º Ïó¥Ï∞®Ïùò ÏàúÏÑúÎ•º Íµ¨Î∂Ñ
                                            const [direction, train] = info.trainLineNm.split(' - ');
                                            // ÌòÑÏû¨ Ïó¥Ï∞®Ïùò Î∞©Î©¥Ïù¥ Ïù¥ÎØ∏ Ï∂úÎ†•Îêú Î∞©Î©¥Ïù∏ÏßÄ ÌôïÏù∏
                                            const directionIndex = printedDirections.indexOf(train);
                                            // Î∞©Î©¥Ïù¥ Ïù¥ÎØ∏ Ï∂úÎ†•ÎêòÏóàÎäîÏßÄ ÌôïÏù∏ÌïòÍ≥† Ï∂úÎ†• Ïó¨Î∂ÄÏóê Îî∞Îùº printedDirections Î∞∞Ïó¥Ïóê Ï∂îÍ∞Ä
                                            if (directionIndex === -1) {
                                                printedDirections.push(train);
                                            }

                                            return (
                                                <div key={info.rowNum} style={{ border: '1px solid black', padding: '10px', margin: '5px' }}>
                                                    <div>
                                                        <p>
                                                            {direction} ({train}) {directionIndex === -1 ? '- Ï≤´ Î≤àÏß∏ Ïó¥Ï∞®' : '- Îëê Î≤àÏß∏ Ïó¥Ï∞®'}
                                                        </p>
                                                    </div>
                                                    <p>
                                                        üëâ {info.recptnDt} Í∏∞Ï§Ä <span style={{ color: 'red' }}>{(info.barvlDt / 60).toFixed(2)}Î∂Ñ</span> Îí§ ÎèÑÏ∞©
                                                    </p>
                                                    <p>‚úî ÌòÑÏúÑÏπò : {info.arvlMsg3}</p>
                                                </div>
                                            );
                                        })}
                                    </div>
                                    <br/>
                                    <hr/>
                                </div>
                            ))}
                            <br/> {/* Î™®Îì† Ï∂úÎ†•Ïù¥ ÏôÑÎ£åÎêú ÌõÑÏóê Ï§ÑÎ∞îÍøà Ï∂îÍ∞Ä */}
                        </div>
                    )}
                </div>
            )}

            {/* stationIdÍ∞Ä Ï°¥Ïû¨ÌïòÎäî Í≤ΩÏö∞ÏóêÎßå ÏïÑÎûò ÎÇ¥Ïö©ÏùÑ ÌëúÏãú */}
            {congestionData && (
                <div>
                    <h2>{congestionData.stationName}Ïùò ÌòºÏû°ÎèÑ Ï†ïÎ≥¥</h2>
                    <p>ÌòºÏû°ÎèÑ: {congestionData.congestion}</p>
                    {/* ÌòºÏû°ÎèÑ Ï†ïÎ≥¥Ïóê Îî∞Îùº Ï∂îÍ∞ÄÏ†ÅÏù∏ ÌëúÏãúÎ•º Ìï† Ïàò ÏûàÏùå */}
                </div>
            )}
        </div>
    );

};
export default Right;
