import React, { useEffect, useState } from 'react';
import Main from '../components/section/Main';
import { useSelector } from 'react-redux';
import { getCurrentTime, getDayType } from '../data/time';
import train from '../assets/img/nav/train.png';
import wait from '../assets/img/nav/waiting.png';
import walk from '../assets/img/nav/walk.png';

const Nav = () => {
    const startStation = useSelector(state => state.startStation);
    const endStation = useSelector(state => state.endStation);
    const [currentTime, setCurrentTime] = useState(getCurrentTime());
    const [dayType, setDayType] = useState(getDayType());
    const [results, setResults] = useState([]);

    useEffect(() => {
        const timer = setInterval(() => {
            setCurrentTime(getCurrentTime());
        }, 1000);
        return () => clearInterval(timer);
    }, []);

    useEffect(() => {
        const fetchDataFromSpring = async () => {
            try {
                const url = `http://localhost:8080/SearchRoute?start=${encodeURIComponent(startStation)}&end=${encodeURIComponent(endStation)}`;
                const response = await fetch(url, {
                    method: "POST",
                    headers: {
                        "Content-Type": "application/json",
                    },
                });
                const data = await response.json();
                setResults(data);
            } catch (error) {
                console.error('데이터 가져오기 실패:', error);
            }
        };
        if (startStation && endStation) {
            fetchDataFromSpring();
        }
    }, [startStation, endStation]);

    const renderResults = () => {
        return results.map((result, index) => (
            <div key={index} className="resultsMap">
                <h1 className="resultsHeader">길찾기 결과 {index + 1}</h1>
                <h3>출발 시간: {result.cuTime}, ({dayType})</h3>
                <div className="visualRepresentation" style={{ width: '1000px', height: '30px', backgroundColor: 'lightgray', margin: '20px 0' }}>
                    {renderTransferBars(result)}
                </div>
                <p className="scheduleTime">도착 시간(시간표): {result.finalTime}</p>
                {result.path && (
                    <>
                        <p className="resultItem">출발지: {result.path[0]}</p>
                        {result.eachTypeOfLine.map((line, index) => (
                            <p key={index} className="resultItem">{index + 1}번 환승: {line}({result.eachTransferStation[index]}) - {(Number(result.fiTime) + Number(result.eachWaitingTime[index]) + Number(result.eachWalkingTime[index]) + Number(result.eachTime[index])).toString()} 열차 탑승</p>
                        ))}
                        <p className="resultItem">도착지: {result.path[result.path.length - 1]}({result.eachTransferStation[result.eachTransferStation.length - 1]})</p>
                    </>
                )}
            </div>
        ));
    };

    const renderTransferBars = (result) => {
        const transferBars = [];
        const iconSize = 20; // 이미지 크기
        let i = 0;
        do {
            const goBarWidth = result.eachTime[i] * ratio;
            const transferBarWidth = result.eachWalkingTime[i] * ratio;
            const waitingBarWidth = result.eachWaitingTime[i] * ratio;

            transferBars.push(
                <div key={`go${i}`} style={{ display: 'inline-block', width: `${goBarWidth}px`, height: '30px', backgroundColor: getLineColor(i === 0 ? result.startLine : result.eachTypeOfLine[i - 1]), position: 'relative', whiteSpace: 'nowrap', overflow: 'visible' }}>
                    <img src={train} alt="subway" style={{ position: 'absolute', left: '50%', top: '50%', transform: 'translate(-50%, -50%)', maxWidth: `${goBarWidth}px`, maxHeight: '100%', width: 'auto', height: 'auto' }} />
                    <span style={{ position: 'absolute', left: '50%', bottom: '-20px', transform: 'translate(-50%, 0)', color: getLineColor(i === 0 ? result.startLine : result.eachTypeOfLine[i - 1]) }}>{result.eachTime[i]}분-{(i === 0 ? startStation : result.eachTypeOfLine[i - 1])}({(i === 0 ? result.startLine : result.eachTransferStation[i - 1])})</span>
                </div>
            );
            if (result.eachWalkingTime[i] !== undefined && result.eachWalkingTime[i] !== null && result.eachWalkingTime[i] !== "") {
                transferBars.push(
                    <div key={`transfer${i}`} style={{ display: 'inline-block', width: `${transferBarWidth}px`, height: '30px', backgroundColor: 'lightgray', position: 'relative', whiteSpace: 'nowrap', overflow: 'visible' }}>
                        <img src={walk} alt="walk" style={{ position: 'absolute', left: '50%', top: '50%', transform: 'translate(-50%, -50%)', maxWidth: `${transferBarWidth}px`, maxHeight: '100%', width: 'auto', height: 'auto' }} />
                        <span style={{ position: 'absolute', left: '50%', bottom: '-20px', transform: 'translate(-50%, 0)', color: '#000' }}>{result.eachWalkingTime[i]}분</span>
                    </div>
                );
            }

            if (result.eachWaitingTime[i] !== undefined && result.eachWaitingTime[i] !== null && result.eachWaitingTime[i] !== "") {
                transferBars.push(
                    <div key={`wait${i}`} style={{ display: 'inline-block', width: `${waitingBarWidth}px`, height: '30px', backgroundColor: 'red', position: 'relative', whiteSpace: 'nowrap', overflow: 'visible' }}>
                        <img src={wait} alt="wait" style={{ position: 'absolute', left: '50%', top: '50%', transform: 'translate(-50%, -50%)', maxWidth: `${waitingBarWidth}px`, maxHeight: '100%', width: 'auto', height: 'auto' }} />
                        <span style={{ position: 'absolute', left: '50%', bottom: '-20px', transform: 'translate(-50%, 0)', color: '#000' }}>{result.eachWaitingTime[i]}분</span>
                    </div>
                );
            }

            i++;
        } while (i < result.eachTime.length - 1);

        if (result.eachTime.length - 1 > 0) {
            const lastGoBarWidth = result.eachTime[result.eachTime.length - 1] * ratio;
            transferBars.push(
                <div key={`go${result.eachTime.length - 1}`} style={{ display: 'inline-block', width: `${lastGoBarWidth}px`, height: '30px', backgroundColor: getLineColor(result.eachTypeOfLine[result.eachTypeOfLine.length - 1]), position: 'relative', whiteSpace: 'nowrap', overflow: 'visible' }}>
                    <img src={train} alt="subway" style={{ position: 'absolute', left: '50%', top: '50%', transform: 'translate(-50%, -50%)', maxWidth: `${lastGoBarWidth}px`, maxHeight: '100%', width: 'auto', height: 'auto' }} />
                    <span style={{ position: 'absolute', left: '50%', bottom: '-20px', transform: 'translate(-50%, 0)', color: getLineColor(result.eachTypeOfLine[result.eachTypeOfLine.length - 1]) }}>{result.eachTime[result.eachTime.length - 1]}분-{(result.eachTypeOfLine[result.eachTypeOfLine.length - 1])}({result.eachTransferStation[result.eachTransferStation.length - 1]})</span>
                </div>
            );
        }
        return transferBars;
    };

    const getLineColor = (line) => {
        const lineColors = {
            '1호선': '#0d3692',
            '2호선': '#33a23d',
            '3호선': '#fe5d10',
            '4호선': '#00a2d1',
            '5호선': '#8b50a4',
            '6호선': '#c55c1d',
            '7호선': '#54640d',
            '8호선': '#f14c82',
        };
        return lineColors[line] || '#000'; // 기본 색상은 검정색
    };

    const ratio = 1000 / results.totalTime || 0;

    return (
        <Main title="실시간 길찾기" description="실시간 길찾기 페이지">
            <div className="resultsContainer">
                <h1 className="resultsHeader">출발지/도착지</h1>
                {startStation && <p className="resultItem">출발지: {startStation}</p>}
                {endStation && <p className="resultItem">도착지: {endStation}</p>}
                {!startStation && <p className="resultItem">출발지 정보가 없습니다.</p>}
                {!endStation && <p className="resultItem">도착지 정보가 없습니다.</p>}
            </div>
            {renderResults()}
        </Main>
    );
};

export default Nav;
